import React, { useEffect, useState } from "react";
import { auth } from "../Config/firebase";
import { getUserProfile, updateUserProgress } from "../Services";

const TrainingPage = () => {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [dailyQuests, setDailyQuests] = useState([]);
    const [weeklyQuests, setWeeklyQuests] = useState([]);
    const [resetTime, setResetTime] = useState({ daily: null, weekly: null });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const user = auth.currentUser;
                if (user) {
                    const profile = await getUserProfile(user.uid);
                    setUserData(profile);
                    generateQuests(profile.level);
                    setupResetTimers();
                }
            } catch (error) {
                console.error("Error fetching user data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchUserData();
    }, []);

    const generateQuests = (level) => {
        const sampleDaily = [
            { id: 1, title: "Sprint Drills", xp: 15 },
            { id: 2, title: "Passing Accuracy", xp: 20 }
        ];
        
        const sampleWeekly = [
            { id: 3, title: "Full Match Simulation", xp: 50 },
            { id: 4, title: "Endurance Challenge", xp: 40 }
        ];

        setDailyQuests(sampleDaily);
        setWeeklyQuests(sampleWeekly);
    };

    const setupResetTimers = () => {
        const now = new Date();
        const dailyReset = new Date();
        dailyReset.setHours(24, 0, 0, 0); // Reset at midnight

        const weeklyReset = new Date();
        weeklyReset.setDate(weeklyReset.getDate() + (7 - weeklyReset.getDay())); // Reset on Sunday
        weeklyReset.setHours(0, 0, 0, 0);

        setResetTime({ daily: dailyReset, weekly: weeklyReset });
    };

    const completeQuest = async (xpGained) => {
        if (!userData) return;
        const updatedXP = userData.xp + xpGained;
        let updatedLevel = userData.level;
        
        if (updatedXP >= 100) {
            updatedLevel += 1;
        }

        await updateUserProgress(auth.currentUser.uid, updatedXP, updatedLevel);
        setUserData({ ...userData, xp: updatedXP, level: updatedLevel });
    };

    return (
        <div style={{ padding: "20px" }}>
            <h2>Training Quests</h2>
            {loading ? <p>Loading...</p> : (
                <div>
                    <h3>Daily Quests (Resets at {resetTime.daily?.toLocaleTimeString()})</h3>
                    {dailyQuests.map((quest) => (
                        <div key={quest.id} style={{ marginBottom: "15px" }}>
                            <p>{quest.title} - XP: {quest.xp}</p>
                            <button 
                                style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}
                                onClick={() => completeQuest(quest.xp)}
                            >
                                Complete Quest
                            </button>
                        </div>
                    ))}

                    <h3>Weekly Quests (Resets at {resetTime.weekly?.toLocaleDateString()})</h3>
                    {weeklyQuests.map((quest) => (
                        <div key={quest.id} style={{ marginBottom: "15px" }}>
                            <p>{quest.title} - XP: {quest.xp}</p>
                            <button 
                                style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}
                                onClick={() => completeQuest(quest.xp)}
                            >
                                Complete Quest
                            </button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default TrainingPage;
