import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { auth } from "../Config/firebase";
import { getUserProfile, updateUserSkills, updateUserProgress } from "../Services";

const SkillTrainingPage = () => {
    const { skill } = useParams(); // Get skill name from URL
    const navigate = useNavigate();
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [dailyQuests, setDailyQuests] = useState([]);
    const [weeklyQuests, setWeeklyQuests] = useState([]);
    const [trainingModules, setTrainingModules] = useState([]);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const user = auth.currentUser;
                if (user) {
                    const profile = await getUserProfile(user.uid);
                    setUserData(profile);
                    generateQuests(profile.level);
                    generateTrainingModules(profile.level);
                }
            } catch (error) {
                console.error("Error fetching user data:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchUserData();
    }, [skill]);

    const questsData = {
        stamina: [
            { id: 1, title: "Endurance Run", xp: 10, skillXp: 20 },
            { id: 2, title: "Sprint Repeats", xp: 15, skillXp: 25 }
        ],
        dribbling: [
            { id: 3, title: "Cone Dribbling", xp: 10, skillXp: 20 },
            { id: 4, title: "Fast Footwork", xp: 15, skillXp: 25 }
        ],
        speed: [
            { id: 5, title: "Acceleration Drills", xp: 10, skillXp: 20 },
            { id: 6, title: "Sprint Training", xp: 15, skillXp: 25 }
        ],
        defending: [
            { id: 7, title: "Defensive Positioning", xp: 10, skillXp: 20 },
            { id: 8, title: "Interception Drills", xp: 15, skillXp: 25 }
        ],
        shooting: [
            { id: 9, title: "Shot Accuracy", xp: 10, skillXp: 20 },
            { id: 10, title: "Power Shooting", xp: 15, skillXp: 25 }
        ],
        passing: [
            { id: 11, title: "Short Passing", xp: 10, skillXp: 20 },
            { id: 12, title: "Long Passing", xp: 15, skillXp: 25 }
        ]
    };

    const generateQuests = (level) => {
        setDailyQuests(questsData[skill] || []);
        setWeeklyQuests((questsData[skill] || []).map(q => ({ ...q, xp: q.xp * 2, skillXp: q.skillXp * 2 }))); // Weekly quests give more XP
    };

    const generateTrainingModules = (level) => {
        const modules = {
            stamina: [
                { id: 1, title: "Beginner Stamina Training", skillXp: 50, requiredLevel: 1 },
                { id: 2, title: "Advanced Stamina Drills", skillXp: 75, requiredLevel: 3 }
            ],
            dribbling: [
                { id: 3, title: "Basic Ball Control", skillXp: 50, requiredLevel: 1 },
                { id: 4, title: "Advanced Dribbling Techniques", skillXp: 75, requiredLevel: 3 }
            ],
        };
        setTrainingModules(modules[skill] || []);
    };

    const completeQuest = async (quest) => {
        if (!userData) return;
        const updatedSkillXP = userData.skills[skill] + quest.skillXp;
        const updatedXP = userData.xp + quest.xp;
        let updatedLevel = userData.level;

        if (updatedXP >= 100) {
            updatedLevel += 1;
        }

        await updateUserSkills(auth.currentUser.uid, { ...userData.skills, [skill]: updatedSkillXP });
        await updateUserProgress(auth.currentUser.uid, updatedXP, updatedLevel);
        setUserData({ ...userData, skills: { ...userData.skills, [skill]: updatedSkillXP }, xp: updatedXP, level: updatedLevel });
    };

    return (
        <div style={{ padding: "20px" }}>
            <h2>{skill.charAt(0).toUpperCase() + skill.slice(1)} Training</h2>
            <button onClick={() => navigate("/")} style={{ marginBottom: "10px" }}>Back to Dashboard</button>
            {loading ? <p>Loading...</p> : (
                <>
                    <h3>Your {skill} Level: {userData?.skills[skill]}</h3>
                    <h3>Daily Quests</h3>
                    {dailyQuests.map((quest) => (
                        <div key={quest.id}>
                            <p>{quest.title} - XP: {quest.xp}, Skill XP: {quest.skillXp}</p>
                            <button onClick={() => completeQuest(quest)}>Complete Quest</button>
                        </div>
                    ))}
                    <h3>Weekly Quests</h3>
                    {weeklyQuests.map((quest) => (
                        <div key={quest.id}>
                            <p>{quest.title} - XP: {quest.xp}, Skill XP: {quest.skillXp}</p>
                            <button onClick={() => completeQuest(quest)}>Complete Quest</button>
                        </div>
                    ))}
                    <h3>Training Modules</h3>
                    {trainingModules.map((module) => (
                        <div key={module.id} style={{ opacity: userData.level >= module.requiredLevel ? 1 : 0.5 }}>
                            <p>{module.title} - Skill XP: {module.skillXp}</p>
                            <button onClick={() => completeQuest(module)} disabled={userData.level < module.requiredLevel}>
                                {userData.level >= module.requiredLevel ? "Complete Module" : `Unlocks at Level ${module.requiredLevel}`}
                            </button>
                        </div>
                    ))}
                </>
            )}
        </div>
    );
};

export default SkillTrainingPage;
